import "./Login.css";
import React, { useState } from "react";
import { sendRequest } from "../../utils/sendRequest";
import { Redirect } from 'react-router';
// import { Redirect } from "react-router-dom"; 
import { useHistory } from "react-router-dom";
function Login() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

const  onSaveFunction =async () =>{ 



     const response = await   sendRequest("logIn", { email: email, password: password }, "post")
     if(response.informations=="true"){
    
   

     
        let history = useHistory();
      
      history.push('/chat')
      
      
      // <Redirect to='/chat' />
     }else{
      alert("false")
     }
}




  return (
    <div className="loginPage">
      <div className="organize">
        <p className="loginString">log in</p>
      </div>
      <div className="inputLogin">
        <input
          type="email"
          className="firstInput"
          placeholder="enterYourEmail"
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          className="secondInput"
          placeholder="enterYourPassword"
          onChange={(s) => setPassword(s.target.value)}
        />
      </div>

      <div className="enterToLogIin">
        <h1
          className="enterToLogIinString"
          onClick={() =>
            onSaveFunction() 

          }
        >
          Log in
        </h1>
        <a className="signInString">sign in</a>
      </div>
    </div>
  );
}

export default Login;
