import React, { useState } from "react";
import "./index.css";
// import { ButtonBase } from "@material-ui/core";
import { sendRequest } from "../utils/sendRequest";

const FileOfMessages = ({ selectedChatingUser }) => {
  useEffect(() => {
   const funct=async() => {
      const GetId = await sendRequest(
        "handleGetById",
        {
          id: selectedChatingUser,
        },
        "POST"
      );
      }
      funct();
  }, []);

  const [getValue, setGetValue] = useState();
  const [copyGetValue, setCopyGetValue] = useState([]);

  const sendMessage = () => {
    setCopyGetValue([getValue, ...copyGetValue]);
  };
  console.log(copyGetValue);

  console.log({ selectedChatingUser });

  return (
    <div className="FileOfMessages">
      <div className="bordering">
        <div className="contectOfTheList">
          <div className="pictures">
            <img
              className="Arrow-picture"
              src="./imagee/Arrow.jpg"
              width="50px"
              height="50px"
            />
          </div>

          <div className="strings">
            <span className="arrow">Arrow</span>
            <span className="ArrowLider">Killer</span>
          </div>
        </div>

        <div className="chat-contect">
          <div className="DateOfTheSeen">
            <p>yesterdey,15,00</p>
          </div>

          {copyGetValue.map(() => {
            <div className="margin">
              {" "}
              <span className="messageISend">hello</span>
            </div>;
          })}

          <div className="DateOfTheSeen">
            <p>yesterdey,15,00</p>
          </div>

          <div className="revers-message">
            <div className="margin">
              {" "}
              <span className="url">hello pro</span>
            </div>
            <div className="margin">
              {" "}
              <span className="url">How are you </span>
            </div>
            <div className="margin">
              {" "}
              <span className="url">hello pro</span>
            </div>
          </div>
        </div>
        <div className="send-messeage">
          <div className="heeeeee">
            {" "}
            <input
              className="input-sending"
              placeholder="type Something..."
              type="text"
              onChange={(e) => {
                setGetValue(e.target.value);
              }}
            />
            <i
              id="send-icon"
              class="far fa-paper-plane"
              onClick={() => sendMessage()}
            ></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileOfMessages;
