import ProfileMenu from "../../Components/ProfileMenu";
import UsersList from "../../Components/UsersList";
import MessagesView from "../../Components/MessagesView";
import "./apo.css";
import { useUser } from "../../hooks/useUser";
import { useState } from "react";
import { useHistory } from "react-router-dom";


export default function ChatApp() {
  const { email, fullName, id } = useUser();
  const [selectedChatingUser, setSelectedChatingUser] = useState();
 const [dialog,setDialog]=useState("1")




 const sureLogOut = ()=>{

   Storage.removeItem("email")
   Storage.removeItem("fullName")
   Storage.removeItem("id")
history.pushState("./signup")


 }

  return (

    <div className="Father">
{dialog == 2 ?
      <div className="dialogOfLogOut">
        <div className="dialog">
          <h1>LogOut</h1>

          <p>are you sure that you want to log out </p>
          <div className="yesNo">
          <div className="yes" onClick={()=>sureLogOut}>
          <i  class="fas fa-check"></i>
          </div>
          <div className="NO" onClick={()=>{setDialog(1)}}>
          <i  class="fas fa-times"></i>
          </div>
          </div>
        </div>
      </div> : null}


      <div className="ComponentCommon">
        <ProfileMenu fullName={fullName} setDialog={setDialog} />
        <UsersList
          selectedChatingUser={selectedChatingUser}
          setSelectedChatingUser={setSelectedChatingUser}
        />

        <MessagesView selectedChatingUser={selectedChatingUser} />
      </div>
    </div>
  );
}
