import React, { useState } from "react";
import "./Setting.css";
import { useUser } from "../../hooks/useUser";
import { sendRequest } from "../../utils/sendRequest";

const Setting = () => {
  const [changeInput, setChangeInput] = useState(1);
  const [oldPassword, setOldPassword] = useState();
  const [newPassword, setNewPassword] = useState();

  const onUpdate = async (req, res) => {
    console.log("On Save Called");

    const response = await sendRequest("updatePassword", {}, "post");
  };

  const { id, email, fullName, password } = useUser();
  return (
    <div className="settingProfile">
      <div className="settingMenu">
        <div>
          <img src="./imagee/Arrow.jpg" className="imageOfProfile" />
        </div>
        <div>
          <h1>{fullName}</h1>
        </div>
        <div className="email">
          <p> {email}</p>
        </div>

        <p
          onClick={() => {
            setChangeInput(2);
          }}
        >
          change password{" "}
        </p>

        {changeInput == 1 ? (
          <p
            onClick={() => {
              setChangeInput(2);
            }}
          >
            change password{" "}
          </p>
        ) : (
          <div>
            <label>enter old password</label>
            <input
              type="string"
              onChange={() => {
                setOldPassword;
              }}
            />

            <label>enter new password</label>
            <input
              type="string"
              onChange={() => {
                setNewPassword;
              }}
            />
          </div>
        )}
      </div>
    </div>
  );
};
export default Setting;
