import React, { useState } from "react";
import "./Setting.css";
import { useUser } from "../../hooks/useUser";
import { sendRequest } from "../../utils/sendRequest";

const Setting = () => {
  const [changeInput, setChangeInput] = useState(1);
  const [oldPassword, setOldPassword] = useState();
  const [newPassword, setNewPassword] = useState();


  const onUpdate = async () => {
    const response = await sendRequest(
      "updatePassword",
      { oldPassword: oldPassword, newPassword: newPassword },
      "post"
    );

    if (response.situation)

    console.log(newPassword);
  };

  const { id, email, fullName, password } = useUser();
  return (
    <div className="settingProfile">
      <div className="settingMenu">
        <div>
          <img src="./imagee/Arrow.jpg" className="imageOfProfile" />
        </div>
        <div>
          <h1>{fullName}</h1>
        </div>
        <div className="email">
          <p> {email}</p>
        </div>

        {changeInput == 1 ? (
          <p
            onClick={() => {
              setChangeInput(2);
            }}
          >
            change password{" "}
          </p>
        ) : (
          <div>
            <label>enter old password</label>
            <input type="string" onChange={(s)=> setOldPassword(s.target.value)} />

            <label>enter new password</label>
            <input type="string" onChange={(s)=>setNewPassword(s.target.value)} />
            <button onClick={() => onUpdate()}>done</button>
          </div>
        )}
      </div>
    </div>
  );
};
export default Setting;
