import React, { useState } from "react";
import "./Setting.css";
import { useUser } from "../../hooks/useUser";
import { sendRequest } from "../../utils/sendRequest";

const Setting = () => {
  const [changeInput, setChangeInput] = useState(1);
  const [oldPassword, setOldPassword] = useState();
  const [newPassword, setNewPassword] = useState();

  const onUpdate = async () => {
    const response = await sendRequest(
      "updatePassword",
      { oldPassword: oldPassword, newPassword: newPassword ,email:email},
      "post"
    );

    if (response.situation == "success") {
      setChangeInput(1);
    } else {
      alert(false);
      setChangeInput(1);
    }

    console.log(newPassword);
  };

  const { id, email, fullName, password } = useUser();
  return (
    <div className="settingProfile">
      <div className="settingMenu">
        <div>
          <img src="./imagee/Arrow.jpg" className="imageOfProfile" />
        </div>
        <div>
          <h1>{fullName}</h1>
        </div>
        <div className="email">
          <p> {email}</p>
        </div>

        {changeInput == 1 ? (
          <p
            onClick={() => {
              setChangeInput(2);
            }}
          >
            change password{" "}
          </p>
        ) : (
          <div>
            <div>
           
            <input className="inputChangePassword"
              type="string" placeholder="old password"
              onChange={(s) => setOldPassword(s.target.value)}
            />
            </div>
          <div className="newPassWord">
            
            <input className="inputChangePassword"
              type="string" placeholder="new password"
              onChange={(s) => setNewPassword(s.target.value)}
            />
            <div className="button">
            <button  onClick={() => onUpdate()}>Change</button>
            </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
export default Setting;
