import React, { useEffect, useState } from "react";
import "./index.css";
// import { ButtonBase } from "@material-ui/core";
import { sendRequest } from "../utils/sendRequest";
import { useUser } from "../hooks/useUser";

const MessagesView = ({ selectedChatingUser }) => {
  const [CurrnetChatingUser, setCurrentChatingUser] = useState("");
  const [messageText, setMessageText] = useState();
  const [messagesList, setMessagesList] = useState([]);

  const { id } = useUser();

  useEffect(() => {
    const funct = async () => {
      const getUser = await sendRequest(
        "userId",
        {
          id: selectedChatingUser,
        },
        "POST"
      );

      const OldMessages = await sendRequest(
        "getOldMessages",
        {
          fromUserId: id,
          toUserId: selectedChatingUser,
        },
        "POST"
      );

      setMessagesList([...OldMessages.messagesList]);
      setCurrentChatingUser(getUser);
    };

    funct();
  }, [selectedChatingUser]);

  // useEffect(() => {
  //   const func = async () => {
  //     const getOldMessagesi = await sendRequest(
  //       "getOldMessages",
  //       {
  //         fromUserId: id,
  //         toUserId: selectedChatingUser,
  //       },
  //       "POST"
  //     );

  //     setMessagesList([getOldMessagesi]);
  //      console.log(messagesList);

  //   };

  //   func();
  // }, []);

  const sendMessage = async () => {
    // console.log({ messageText, fromUserId: id, toUserId: selectedChatingUser });

    const respone = await sendRequest(
      "message",
      {
        messageText: messageText,
        fromUserId: id,
        toUserId: selectedChatingUser,
      },
      "post"
    );
    setMessagesList([messageText, ...messagesList]);
  };

  console.log(messagesList);

  return (
    <div className="FileOfMessages">
      <div className="bordering">
        <div className="contectOfTheList">
          <div className="pictures">
            <img
              className="Arrow-picture"
              src="./imagee/Arrow.jpg"
              width="50px"
              height="50px"
            />
          </div>

          <div className="strings">
            <span className="arrow">{CurrnetChatingUser.fullName}</span>
            <span className="ArrowLider"></span>
          </div>
        </div>

        {messagesList.map((y) => 
          <div className="chat-contect">
            
         (y.fromUserId == id ? null : )   
            <div   className={` y.fromUserId == id ? "margin":"margino" `}>
              <span className="messageISend">{y.message}</span>
            </div> 
            
            

            className={`listOfTheContents ${
              selectedChatingUser === e._id ? "with-border" : ""
            }`}





          </div>
        )}

        <div className="send-messeage">
          <div className="heeeeee">
            <input
              className="input-sending"
              placeholder="type Something..."
              type="text"
              onChange={(e) => {
                setMessageText(e.target.value);
              }}
            />
            <i
              id="send-icon"
              class="far fa-paper-plane"
              onClick={() => sendMessage()}
            ></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessagesView;
